(269, NULL, 1, 263, 1, 1, 'Museum Lecture Series', 'museum-lecture-series', 'Museum Lecture Series', 'Museum Lecture Series', 'Museum Lecture Series', '<p>Museum Lecture Series</p>', '', '', NULL, NULL, 3, 4, NULL, 0, 'pMuseum Lecture Series/p', '2024-12-10 05:19:14', '2024-12-10 05:19:14'),
(270, NULL, 1, 269, 1, 1, 'Annual IGRMS Lecture', 'annual-igrms-lecture', 'Annual IGRMS Lecture', 'Annual IGRMS Lecture', 'Annual IGRMS Lecture', '<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p>The Indira Gandhi Rashtriya Manav Sangrahalaya initiated its prestigious Annual Lecture Series in 2005, in conjunction with the museum&rsquo;s Foundation Day celebrations. This series aims to foster intellectual engagement by exploring a diverse range of topics within Anthropology, Archaeology, Museology, and other related disciplines. Each year, distinguished scholars and experts are invited to present thought-provoking lectures, offering unique insights into these fields. The Annual Lecture Series has become a significant platform for academic exchange and public discourse, enriching the understanding of human cultural heritage.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>', '', '', NULL, NULL, 3, 4, NULL, 0, 'pAnnual IGRMS Lecture/p', '2024-12-10 05:20:19', '2024-12-31 02:32:43'),
